import React, { Component } from 'react';

import axios from 'axios';

class CreateProduct extends Component {


  constructor (){
        super();
        this.state = {
            id: '',
            productCategory: '',
            productExpirationDate: '',
            productName: '',
            productPrice: '',
        };
        this.handleInput = this.handleInput.bind(this);    //actualiza los aributos
        this.handleSubmit = this.handleSubmit.bind(this);
        this.create = this.create.bind(this);

    }

    handleInput(e){                        // funcion evento
        const {value, name} = e.target
        this.setState({                     // cambia dato establecido en el estad0
          [name]: value
        })
        console.log(this.state);
      }









  render() {
      if(!this.state.ready){
            return(
                <div> <h1> Cargando ...</h1></div>
            )
      }else{
          return(
          <div>
            {this.state.products.map((elem) => {
                    return(


                        <div className = "card">
                        <form className = "card-body" onSubmit = {this.handleSubmit}>
                          <div className = "form-group">
                            <input
                              type = "text"
                              name = "id"
                              className = "form-control"
                              placeholder = "codigo"
                              onChange = {this.handleInput}
                            />
                          </div>
                          <div className = "form-group">
                            <input
                              type = "text"
                              name = "productName"
                              className = "form-control"
                              placeholder = "nombre"
                              onChange = {this.handleInput}
                            />
                          </div>
                          <div className = "form-group">
                            <input
                              type = "text"
                              name = "productExpirationDate"
                              className = "form-control"
                              placeholder = "fechaven"
                              onChange = {this.handleInput}
                            />
                          </div>
                          <div className = "form-group">
                            <input
                              type = "text"
                              name = "productCategory"
                              className = "form-control"
                              placeholder = "categoria"
                              onChange = {this.handleInput}
                            />
                          </div>
                          <div className = "form-group">
                            <input
                              type = "text"
                              name = "productPrice"
                              className = "form-control"
                              placeholder = "precio"
                              onChange = {this.handleInput}
                            />
                          </div>
                          <button type="submit" className="btn btn-primary" onClick={this.create}>
                            Ingresar
                          </button>
                        </form>
                      </div>    

                );
            })}
          </div>
          );
      }
    }





  create() {
    axios.post('http://206.189.198.100:8081/backend-0.0.1-SNAPSHOT/app/create_product',this.state)
      .then(res => {
        console.log("Ahi funciono soy un perkinaso ")
      })
  }

}

export default CreateProduct;
